import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getNotesByStudentId } from '../services/noteService';
import AddNote from './AddNote'; // Import AddNote component

const StudentDetails = () => {
  const { id } = useParams();
  const [notes, setNotes] = useState([]);

  // Fetch notes for the student
  useEffect(() => {
    const fetchNotes = async () => {
      const data = await getNotesByStudentId(id);
      setNotes(data);
    };
    fetchNotes();
  }, [id]);

  return (
      <div>
        <h1>Student Notes</h1>

        {/* Notes Table */}
        <table>
          <thead>
          <tr>
            <th>Course Name</th>
            <th>Grade</th>
          </tr>
          </thead>
          <tbody>
          {notes.map((note) => (
              <tr
                  key={note.id}
                  style={{
                    backgroundColor: note.grade > 10 ? 'lightgreen' : 'lightcoral',
                  }}
              >
                <td>{note.courseName}</td>
                <td>{note.grade}</td>
              </tr>
          ))}
          </tbody>
        </table>

        {/* Add Note Form */}
        <AddNote refreshNotes={fetchNotes} />
      </div>
  );
};

export default StudentDetails;
